// frontend/src/RegisterOrgDebug.js
import React, {useState} from 'react';
import axios from 'axios';
import { API_URL } from './api';

export default function RegisterOrgDebug({onMessage}) {
  const [orgName, setOrgName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  async function submit(e) {
    e.preventDefault();
    try {
      const res = await axios.post(`${API_URL}/auth/register`,
        { orgName, email, password },
        { headers: { 'Content-Type': 'application/json' } }
      );
      onMessage('Registered: ' + JSON.stringify(res.data));
    } catch (err) {
      console.error('Register error (full):', err);
      // show server response body if available
      const serverMsg = err.response?.data || err.message;
      onMessage('Register failed: ' + JSON.stringify(serverMsg));
    }
  }

  return (
    <form onSubmit={submit}>
      <h3>Create Organization (debug)</h3>
      <input placeholder="Organization name" value={orgName} onChange={e=>setOrgName(e.target.value)} required />
      <input placeholder="admin email" value={email} onChange={e=>setEmail(e.target.value)} required />
      <input placeholder="password" type="password" value={password} onChange={e=>setPassword(e.target.value)} required />
      <button type="submit">Create Org</button>
    </form>
  );
}
