import React, { useState } from 'react';
import { post } from '../api';

export default function RegisterOrg({ onRegistered }) {
  const [orgName, setOrgName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [adminName, setAdminName] = useState('');

  const submit = async () => {
    if (!orgName || !email || !password)
      return alert('Fill required fields');

    try {
      const res = await post('/auth/register', {
        orgName,
        email,
        password,
        adminName   // optional, backend ignores if not needed
      });

      onRegistered(res);
    } catch (err) {
      console.error(err);
      alert(err?.response?.data?.error || 'Register failed');
    }
  };

  return (
    <div>
      <h3>Register Organization</h3>

      <div className="form-row">
        <input className="input" placeholder="Organization Name"
          value={orgName} onChange={e => setOrgName(e.target.value)} />
      </div>

      <div className="form-row">
        <input className="input" placeholder="Admin Name"
          value={adminName} onChange={e => setAdminName(e.target.value)} />
      </div>

      <div className="form-row">
        <input className="input" placeholder="Admin Email"
          value={email} onChange={e => setEmail(e.target.value)} />
      </div>

      <div className="form-row">
        <input className="input" type="password"
          placeholder="Admin Password"
          value={password}
          onChange={e => setPassword(e.target.value)} />
      </div>

      <div>
        <button className="btn" onClick={submit}>Register</button>
      </div>
    </div>
  );
}
